mapper.select:

	select rpost.*
	from (
	        select post.*, rownum as rnum
	        from (
	                select * 
	                
	                order by b_post_date desc
	            )post 
	        )rpost
	
	where rnum >= ${boardPage.startRow} and rnum <= ${boardPage.endRow}


	select rpost.*
	from (
	        select post.*, rownum as rnum
	        from (
	                select * 
	                
	                order by b_comm_date desc
	            )post 
	        )rpost
	
	where rnum >= ${boardPage.startRow} and rnum <= ${boardPage.endRow}
