mapper.select:

	select *
	from (
			select cb.*, rownum as rnum 
			from (
					select * from crew_board where c_no = ${crewBoardVo.c_no} 
					
					where c_b_content like '%'||#{searchWord}||'%'
					
					order by c_b_date desc 
				)cb
		)
	
	where rnum >= ${boardPage.startRow} and rnum <= ${boardPage.endRow}
