mapper.select:
select c.* from
(select * from crew where c_sdate <= sysdate and sysdate <=c_edate) c, crew_entry e
where c.c_no = e.c_no and e.u_no =#{u_no} and c.u_no = #{u_no}

select c.* from
(select * from crew where sysdate > c_edate) c, crew_entry e
where c.c_no = e.c_no and e.u_no =#{u_no} and c.u_no= #{u_no}
